const S_TABLE_X_TYPE* KpdParaTableGetTableAddr(WORD wGrpIdx, WORD wTableIdx)
{
	const S_TABLE_X_TYPE* pstTable;

	switch(wGrpIdx)
	{
	case GROUP_MAK:
		pstTable = &t_astMAKgrp[wTableIdx];
		break;
	case GROUP_DRV:
		pstTable = &t_astDRVgrp[wTableIdx];
		break;
	case GROUP_BAS:
		pstTable = &t_astBASgrp[wTableIdx];
		break;
	case GROUP_ADV:
		pstTable = &t_astADVgrp[wTableIdx];
		break;
	case GROUP_CON:
		pstTable = &t_astCONgrp[wTableIdx];
		break;
	case GROUP_IN:
		pstTable = &t_astINgrp[wTableIdx];
		break;
	case GROUP_OUT:
		pstTable = &t_astOUTgrp[wTableIdx];
		break;
	case GROUP_COM:
		pstTable = &t_astCOMgrp[wTableIdx];
		break;
	case GROUP_PID:
		pstTable = &t_astPIDgrp[wTableIdx];
		break;
	case GROUP_PRT:
		pstTable = &t_astPRTgrp[wTableIdx];
		break;
	case GROUP_ENC:
		pstTable = &t_astENCgrp[wTableIdx];
		break;
	case GROUP_M2:
		pstTable = &t_astM2grp[wTableIdx];
		break;
	default:
		pstTable = NULL;
		break;
	}
	return pstTable;
}


